  }

  // --- ANMELDUNGEN ---
  static async getAnmeldungen(): Promise<Anmeldungen[]> {
    const data = await callApi('GET', `/apps/${APP_IDS.ANMELDUNGEN}/records`);
    return Object.entries(data).map(([id, rec]: [string, any]) => ({
